INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2015-04-02 11:24:21', '2015-04-02 11:24:21', 'Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'trash', 'open', 'open', '', 'hello-world', '', '', '2015-04-02 11:41:23', '2015-04-02 11:41:23', '', 0, 'http://scroungr.dev/wp/?p=1', 0, 'post', '', 0),
(2, 1, '2015-04-02 11:24:21', '2015-04-02 11:24:21', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://scroungr.dev/wp/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'trash', 'open', 'open', '', 'sample-page', '', '', '2015-04-02 16:03:07', '2015-04-02 16:03:07', '', 0, 'http://scroungr.dev/wp/?page_id=2', 0, 'page', '', 0),
(3, 1, '2015-04-02 11:27:24', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2015-04-02 11:27:24', '0000-00-00 00:00:00', '', 0, 'http://scroungr.dev/wp/?p=3', 0, 'post', '', 0),
(5, 1, '2015-03-31 22:10:57', '2015-04-01 02:10:57', 'Use the form below to submit an add request for any application you''d like to see in the catalog:\r\n\r\n[user-submitted-posts]', 'Add An App', '', 'publish', 'closed', 'closed', '', 'addanapp', '', '', '2015-04-02 13:41:05', '2015-04-02 17:41:05', '', 0, 'http://scroungr.wordpress.com/?page_id=1', 0, 'page', '', 0),
(6, 1, '2015-03-31 22:10:58', '2015-03-31 22:10:58', '<strong>Price:</strong> $2.99\r\n<strong>Developers:</strong> Bob and Gertie\r\n<strong>Applicable OS/version(s):</strong> iOS 8.1+\r\n<a href="https://itunes.apple.com/us/genre/ios/id36?mt=8" target="_blank">See it in the app store</a>', 'Sample iOS App', '', 'trash', 'open', 'open', '', 'hello-world-2', '', '', '2015-04-07 06:38:53', '2015-04-07 10:38:53', '', 0, 'http://scroungr.wordpress.com/?p=1', 0, 'post', '', 0),
